mapper.select:
SELECT COUNT(*) AS CNT
FROM BLOG_BM
WHERE BM_ID = #{bmId}

SELECT BM_NO, BM_NM 
FROM BLOG_BM
WHERE DEL_YN = 1
AND BM_ID = #{bmId}
AND BM_PW = #{bmPw}

SELECT B.B_NO, B.B_TITLE,B.BM_NM,B.B_DT,B.B_HIT FROM( SELECT B.B_NO,B.B_TITLE,BM.BM_NM,(TO_CHAR( B.B_DT,'YYYY')||'년 '||TO_CHAR( B.B_DT,'MM')||'월 ' ||TO_CHAR( B.B_DT,'DD')||'일 '||TO_CHAR( B.B_DT,'HH')||'시 '||TO_CHAR( B.B_DT,'MM')||'분 '|| TO_CHAR( B.B_DT,'SS')||'초') AS B_DT ,B.B_HIT, ROW_NUMBER() OVER(ORDER BY B.B_NO DESC) AS RNUM FROM BLOG_B B INNER JOIN BLOG_BM BM ON B.BM_NO = BM.BM_NO AND BM.DEL_YN = 1 WHERE B.DEL_YN = 1
AND B.B_TITLE LIKE '%' || #{searchTxt} || '%'
AND BM.BM_NM LIKE '%' || #{searchTxt} || '%'
AND (B.B_TITLE LIKE '%' || #{searchTxt} || '%' OR BM.BM_NM LIKE '%' || #{searchTxt} || '%')
)B WHERE B.RNUM BETWEEN #{startCnt} AND #{endCnt}

SELECT B.B_NO, B.B_TITLE,B.BM_NM,B.B_DT,B.B_HIT,B.BM_NO,B.B_CON FROM( SELECT B.B_NO,B.B_TITLE,BM.BM_NM,BM.BM_NO,B.B_CON,(TO_CHAR( B.B_DT,'YYYY.MM.DD')) AS B_DT ,B.B_HIT, ROW_NUMBER() OVER(ORDER BY B.B_NO DESC) AS RNUM FROM BLOG_B B INNER JOIN BLOG_BM BM ON B.BM_NO = BM.BM_NO AND BM.DEL_YN = 1 WHERE B.DEL_YN = 1
AND B.B_TITLE LIKE '%' || #{searchTxt} || '%'
AND BM.BM_NM LIKE '%' || #{searchTxt} || '%'
AND (B.B_TITLE LIKE '%' || #{searchTxt} || '%' OR BM.BM_NM LIKE '%' || #{searchTxt} || '%')
)B WHERE B.RNUM BETWEEN #{startCnt} AND #{endCnt} AND B.BM_NO = #{bmno}
